mapper.select:
SELECT COUNT(*) > 0
FROM chat_join
WHERE room_id = #{roomId} AND user_num = #{userNum}

SELECT room_id AS roomId, user_num AS userNum
FROM chat_join
WHERE room_id = #{roomId}

SELECT user_num
FROM chat_join
WHERE room_id = #{roomId} and user_num != #{userNum}

mapper.delete:
UPDATE chat_join
SET is_join = 0
WHERE room_id = #{roomId}
AND user_num = #{userNum}
