mapper.select:
SELECT board_content
FROM sellbuy_board
WHERE board_id = #{boardId}

mapper.update:
UPDATE sellbuy_board
SET is_del = 'Y'
WHERE board_id = #{boardId}

update sellbuy_board
set read_count = read_count+1
where board_id = #{boardId}

UPDATE sellbuy_board
SET like_count = like_count + 1
WHERE board_id = #{boardId}

UPDATE sellbuy_board
SET like_count = like_count - 1
WHERE board_id = #{boardId}
