mapper.select:
SELECT qna_content
FROM qna_board
WHERE qna_id = #{qnaId}

mapper.update:
UPDATE qna_board
SET
qna_name = #{qnaName},
category = #{category},
qna_content = #{qnaContent}
WHERE qna_id = #{qnaId}

mapper.select:
select * from qna_board
WHERE is_del = 'N' AND user_id = #{userNum}
order by qna_id desc
